function validate(){let e=document.querySelector(".name"),t=document.querySelector(".email"),a=document.querySelector(".message"),l=document.querySelector(".submit");const r=/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;l.addEventListener("click",l=>{l.preventDefault(),""==e.value||""==a.value?emptyerror():r.test(t.value.trim())?(sendmail(e.value,t.value,a.value),success(),e.value="",t.value="",a.value=""):error()})}function sendmail(e,t,a){emailjs.send("service_qxqxyxi","template_njaj7of",{to_name:e,from_name:t,message:a})}function emptyerror(){Swal.fire({icon:"error",title:"Oops...",text:"Please fill out the required fields!"})}function error(){Swal.fire({icon:"error",title:"Oops...",text:"Please enter a valid email address!"})}function success(){Swal.fire({icon:"success",title:"Sent!",text:"Message successfully sent!"})}validate();